/* eslint-disable max-classes-per-file */
/* eslint-disable import/no-default-export */

// for silencing errors on static imports using the plugin-static.js
declare module "*.svg" {
  /** The static asset's url */
  const href: string
  export default href
}

declare module "*.svg?inline" {
  /** The static asset's url */
  const href: string
  export default href
}
